sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var requestUser = request.pathParams.user_name;
    var requestEncodedQuery = request.queryParams.ni_query;

    var user = new GlideRecord('sys_user');
    user.get('user_name', requestUser);

	if (!user.user_name) {
		var error = new sn_ws_err.ServiceError();

		error.setStatus(404);
		error.setMessage("User not found");
		error.setDetail("No user record found for the user_name passed into the ni_info web service resource using the user_name path parameter.");

		response.setError(error);
		return;
	}

    var userNeedItCount = new GlideAggregate('x_58872_needit_needit');

    userNeedItCount.addAggregate('COUNT');
    userNeedItCount.addQuery('u_requested_for', user.sys_id);
    userNeedItCount.addEncodedQuery(requestEncodedQuery);
    userNeedItCount.groupBy('u_request_type');
    userNeedItCount.query();

    var userRecords = [];

    while (userNeedItCount.next()) {
        var currentRecordsCount = userNeedItCount.getAggregate('COUNT');

        var currentRequestType = userNeedItCount.u_request_type.getDisplayValue();

        userRecords.push({
            requestType: currentRequestType,
            count: currentRecordsCount
        });
    }

    var body = {
        totalRecords: userRecords.length,
        records: userRecords,
        user: {
            "User name": user.user_name,
            "User ID": user.sys_id,
        },
    };

    response.setBody(body);

})(request, response);
